// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [token, setToken] = useState(null);
  const [stocks, setStocks] = useState(null);

  useEffect(() => {
    const hash = window.location.hash;
    if (hash) {
      const tokenFromUrl = new URLSearchParams(hash.substring(1)).get('id_token');
      if (tokenFromUrl) {
        localStorage.setItem('token', tokenFromUrl);
        setToken(tokenFromUrl);
        window.location.hash = '';
      }
    } else {
      const savedToken = localStorage.getItem('token');
      if (savedToken) setToken(savedToken);
    }
  }, []);

  useEffect(() => {
    if (token) {
      axios.get('http://<your-ec2-ip>:5000/api/stocks', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      .then(res => setStocks(res.data))
      .catch(err => console.error(err));
    }
  }, [token]);

  const login = () => {
    window.location.href = `https://<your-cognito-domain>.auth.<region>.amazoncognito.com/login?response_type=token&client_id=<your-client-id>&redirect_uri=http://localhost:3000`;
  }

  if (!token) return <button onClick={login}>Login with Cognito</button>;
  if (!stocks) return <p>Loading stock data...</p>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Stock Prices</h1>
      <ul>
        {Object.entries(stocks).map(([symbol, price]) => (
          <li key={symbol}>{symbol}: ${price}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
