import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [token, setToken] = useState(null);
  const [stocks, setStocks] = useState(null);
  const [symbol, setSymbol] = useState("");
  const [searchedStock, setSearchedStock] = useState(null);
  const [portfolio, setPortfolio] = useState([]);
  const [priceHistory, setPriceHistory] = useState({});

  const logout = () => {
    localStorage.removeItem("id_token");
    const logoutUrl =`https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/logout` +
    `?client_id=1c5q0l5q7mrksqofaeq39j3ukh` +
    `&redirect_uri=http://localhost:3000&response_type=token&scope=email+openid+phone`;
    window.location.href = logoutUrl;
  };

  const login = () => {
    window.location.href =
    "https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/login" +
    "?response_type=token" +
    "&client_id=1c5q0l5q7mrksqofaeq39j3ukh" +
    "&redirect_uri=http://localhost:3000/" +
    "&scope=email+openid+phone";
  };

  useEffect(() => {
    const hash = window.location.hash;
    if (hash && hash.includes("id_token")) {
      const params = new URLSearchParams(hash.slice(1));
      const idToken = params.get("id_token");
      if (idToken) {
        localStorage.setItem("id_token", idToken);
        setToken(idToken);
        window.history.replaceState({}, document.title, window.location.pathname);
      }
    } else {
      const savedToken = localStorage.getItem("id_token");
      if (savedToken) setToken(savedToken);
    }
  }, []);

  useEffect(() => {
    if (token) {
      axios
        .get("http://127.0.0.1:5000/api/stocks", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setStocks(res.data))
        .catch((err) => console.error("API Error", err));
    }
  }, [token]);

  const searchStock = () => {
    if (!symbol) return;
    axios
      .get(`http://127.0.0.1:5000/api/stocks?symbol=${symbol}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSearchedStock(res.data))
      .catch((err) => console.error("Stock search failed", err));
  };

  const addToPortfolio = () => {
    if (!portfolio.includes(symbol)) {
      setPortfolio([...portfolio, symbol]);
    }
    setSymbol("");
  };

  useEffect(() => {
    portfolio.forEach((sym) => {
      if (!priceHistory[sym]) {
        axios
          .get(`http://127.0.0.1:5000/api/stocks/history?symbol=${sym}`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            setPriceHistory((prev) => ({ ...prev, [sym]: res.data }));
          })
          .catch((err) => console.error("Price history error", err));
      }
    });
  }, [portfolio, token]);

  if (!token) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-center">
        <h2 className="text-3xl font-semibold mb-4">🔐 Please Login to Continue</h2>
        <button
          onClick={login}
          className="bg-blue-600 text-white px-6 py-2 rounded shadow hover:bg-blue-700"
        >
          Login with Cognito
        </button>
      </div>
    );
  }

  if (!stocks) return <p className="text-center mt-10">Loading stock data...</p>;

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-6 font-sans">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-4xl font-bold">Stock Tracker Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
        >
          Logout
        </button>
      </header>

      <section className="mb-10">
        <div className="flex items-center space-x-4 mb-4">
          <input
            type="text"
            placeholder="Enter stock symbol (e.g. AAPL)"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
            className="border px-3 py-2 rounded shadow-sm w-64"
          />
          <button
            onClick={searchStock}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Search
          </button>
          <button
            onClick={addToPortfolio}
            className="bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-600"
          >
            Add to Portfolio
          </button>
        </div>

        {searchedStock && (
          <div className="bg-white p-4 rounded shadow w-full md:w-1/2">
            <h3 className="text-xl font-semibold">🔎 {searchedStock.symbol}</h3>
            <p className="text-gray-700">Price: ${searchedStock.price}</p>
            <p className="text-gray-500">Change: {searchedStock.change}%</p>
          </div>
        )}
      </section>

      <section className="mb-10">
        <h2 className="text-2xl font-semibold mb-4">💼 My Portfolio</h2>
        {portfolio.length === 0 ? (
          <p className="text-gray-500">Your portfolio is empty.</p>
        ) : (
          portfolio.map((sym) => (
            <div key={sym} className="mb-8">
              <h3 className="text-lg font-semibold mb-2">{sym} - Price History</h3>
              {priceHistory[sym] ? (
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={priceHistory[sym]}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis domain={["auto", "auto"]} />
                    <Tooltip />
                    <Line type="monotone" dataKey="price" stroke="#10b981" strokeWidth={2} />
                  </LineChart>
                </ResponsiveContainer>
              ) : (
                <p>Loading chart...</p>
              )}
            </div>
          ))
        )}
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">🌐 All Tracked Stocks</h2>
        <ul className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {Object.entries(stocks).map(([symbol, price]) => (
            <li
              key={symbol}
              className="bg-white p-4 rounded shadow text-center text-lg font-medium"
            >
              {symbol}: ${price}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default App;
