import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [token, setToken] = useState(null);
  const [stocks, setStocks] = useState(null);

  const logout = () => {
    localStorage.removeItem("id_token");

    // Cognito Hosted UI logout
    const logoutUrl = `https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/logout` +
      `?client_id=1c5q0l5q7mrksqofaeq39j3ukh` +
      `&logout_uri=http://localhost:3000`;

    window.location.href = logoutUrl;
  };

  useEffect(() => {
    const hash = window.location.hash;
    if (hash && hash.includes("id_token")) {
      const params = new URLSearchParams(hash.slice(1));
      const idToken = params.get("id_token");

      if (idToken) {
        localStorage.setItem("id_token", idToken);
        setToken(idToken);

        // Clean the URL after saving
        window.history.replaceState({}, document.title, window.location.pathname);
      }
    } else {
      const savedToken = localStorage.getItem("id_token");
      if (savedToken) setToken(savedToken);
    }
  }, []);

  useEffect(() => {
    if (token) {
      axios
        .get("http://localhost:5000/api/stocks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setStocks(res.data))
        .catch((err) => console.error("API Error", err));
    }
  }, [token]);

  const login = () => {
    window.location.href = `https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/login?response_type=token&client_id=1c5q0l5q7mrksqofaeq39j3ukh&redirect_uri=http://localhost:3000/&response_type=token&scope=email+openid+phone`;
  };

  if (!token) return <button onClick={login}>Login with Cognito</button>;

  <button onClick={logout}> Logout</button>
  if (!stocks) return <p>Loading stock data...</p> ;

  return (
    <div className="p-4">

      <h1 className="text-2xl font-bold mb-4">Stock Prices</h1>
      <ul>
        {Object.entries(stocks).map(([symbol, price]) => (
          <li key={symbol}>{symbol}: ${price}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
