import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [token, setToken] = useState(null);
  const [stocks, setStocks] = useState(null);

  const logout = () => {
    localStorage.removeItem("id_token");

    // Cognito Hosted UI logout
    const logoutUrl =
      "https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/logout" +
      "?client_id=1c5q0l5q7mrksqofaeq39j3ukh" +
      "&logout_uri=http://localhost:3000";

    window.location.href = logoutUrl;
  };

  const login = () => {
    window.location.href =
      "https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/login" +
      "?response_type=token" +
      "&client_id=1c5q0l5q7mrksqofaeq39j3ukh" +
      "&redirect_uri=http://localhost:3000" +
      "&scope=email+openid+phone";
  };

  // On initial load â€” parse token from URL or restore from localStorage
  useEffect(() => {
    const hash = window.location.hash;
    if (hash && hash.includes("id_token")) {
      const params = new URLSearchParams(hash.slice(1));
      const idToken = params.get("id_token");

      if (idToken) {
        localStorage.setItem("id_token", idToken);
        setToken(idToken);

        // Clean the URL
        window.history.replaceState({}, document.title, window.location.pathname);
      }
    } else {
      const savedToken = localStorage.getItem("id_token");
      if (savedToken) setToken(savedToken);
    }
  }, []);

  // Fetch stock prices from backend if token is set
  useEffect(() => {
    if (token) {
      axios
        .get("http://127.0.0.1:5000/api/stocks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setStocks(res.data))
        .catch((err) => console.error("API Error", err));
    }
  }, [token]);

  // Login button shown if not authenticated
  if (!token) {
    return (
      <div style={{ padding: "2rem", fontFamily: "Arial" }}>
        <h2>Please login to access stock data</h2>
        <button
          onClick={login}
          style={{
            padding: "10px 20px",
            backgroundColor: "#0275d8",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Login with Cognito
        </button>
      </div>
    );
  }

  // Loading message if stocks not fetched yet
  if (!stocks) return <p>Loading stock data...</p>;

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1 style={{ fontSize: "24px", marginBottom: "1rem" }}>ðŸ“ˆ Stock Prices</h1>
      <button
        onClick={logout}
        style={{
          marginBottom: "1rem",
          padding: "10px 20px",
          border: "none",
          backgroundColor: "#d9534f",
          color: "white",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Logout
      </button>
      <ul>
        {Object.entries(stocks).map(([symbol, price]) => (
          <li key={symbol} style={{ marginBottom: "0.5rem" }}>
            {symbol}: ${price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
