import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [token, setToken] = useState(null);
  const [stocks, setStocks] = useState(null);
  const [symbol, setSymbol] = useState("");
  const [searchedStock, setSearchedStock] = useState(null);
  const [portfolio, setPortfolio] = useState([]);
  const [priceHistory, setPriceHistory] = useState({});

  const logout = () => {
    localStorage.removeItem("id_token");

  // Cognito Hosted UI logout
  const logoutUrl = `https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/logout` +
  `?client_id=1c5q0l5q7mrksqofaeq39j3ukh` +
  `&redirect_uri=http://localhost:3000&response_type=token&scope=email+openid+phone`;

window.location.href = logoutUrl;
};

const login = () => {
window.location.href =
  "https://us-east-2ffnvtooil.auth.us-east-2.amazoncognito.com/login" +
  "?response_type=token" +
  "&client_id=1c5q0l5q7mrksqofaeq39j3ukh" +
  "&redirect_uri=http://localhost:3000/" +
  "&scope=email+openid+phone";
};

  useEffect(() => {
    const hash = window.location.hash;
    if (hash && hash.includes("id_token")) {
      const params = new URLSearchParams(hash.slice(1));
      const idToken = params.get("id_token");

      if (idToken) {
        localStorage.setItem("id_token", idToken);
        setToken(idToken);

        window.history.replaceState({}, document.title, window.location.pathname);
      }
    } else {
      const savedToken = localStorage.getItem("id_token");
      if (savedToken) setToken(savedToken);
    }
  }, []);

  useEffect(() => {
    if (token) {
      axios
        .get("http://127.0.0.1:5000/api/stocks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setStocks(res.data))
        .catch((err) => console.error("API Error", err));
    }
  }, [token]);

  const searchStock = () => {
    if (!symbol) return;
    axios
      .get(`http://127.0.0.1:5000/api/stocks?symbol=${symbol}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSearchedStock(res.data))
      .catch((err) => console.error("Stock search failed", err));
  };

  const addToPortfolio = () => {
    if (!portfolio.includes(symbol)) {
      setPortfolio([...portfolio, symbol]);
    }
    setSymbol("");
  };

  useEffect(() => {
    portfolio.forEach((sym) => {
      if (!priceHistory[sym]) {
        axios
          .get(`http://127.0.0.1:5000/api/stocks/history?symbol=${sym}`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            setPriceHistory((prev) => ({ ...prev, [sym]: res.data }));
          })
          .catch((err) => console.error("Price history error", err));
      }
    });
  }, [portfolio, token]);

  if (!token) {
    return (
      <div style={{ padding: "2rem", fontFamily: "Arial" }}>
        <h2>Please login to access stock data</h2>
        <button
          onClick={login}
          style={{
            padding: "10px 20px",
            backgroundColor: "#0275d8",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Login with Cognito
        </button>
      </div>
    );
  }

  if (!stocks) return <p>Loading stock data...</p>;

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1 style={{ fontSize: "24px", marginBottom: "1rem" }}>ðŸ“ˆ Stock Prices</h1>
      <button
        onClick={logout}
        style={{
          marginBottom: "1rem",
          padding: "10px 20px",
          border: "none",
          backgroundColor: "#d9534f",
          color: "white",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Logout
      </button>

      <div style={{ marginBottom: "2rem" }}>
        <input
          type="text"
          placeholder="Enter stock symbol"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value.toUpperCase())}
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <button onClick={searchStock}>Search</button>
        <button onClick={addToPortfolio} style={{ marginLeft: "10px" }}>Add to Portfolio</button>
        {searchedStock && (
          <div style={{ marginTop: "1rem" }}>
            <h3>{searchedStock.symbol}</h3>
            <p>Price: ${searchedStock.price}</p>
            <p>Change: {searchedStock.change}%</p>
          </div>
        )}
      </div>

      <h2>ðŸ“‹ My Portfolio</h2>
      {portfolio.map((sym) => (
        <div key={sym} style={{ marginBottom: "2rem" }}>
          <h3>{sym} - Price History</h3>
          {priceHistory[sym] ? (
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={priceHistory[sym]}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis domain={["auto", "auto"]} />
                <Tooltip />
                <Line type="monotone" dataKey="price" stroke="#8884d8" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <p>Loading chart...</p>
          )}
        </div>
      ))}

      <h2>ðŸ“Š All Tracked Stocks</h2>
      <ul>
        {Object.entries(stocks).map(([symbol, price]) => (
          <li key={symbol}>{symbol}: ${price}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
